Restaurant.create(name:'Minnies',summary:'We offer a value experience during the day with cashiers and gourmet plate lunches in green styrofoam plates, we have our own fish market with a poke bar and our home smoked fish. A night we offer full service with a full bar and plein air dining by harbor lights. If fish is your passion, please check us out.',hours:'SUN-MON: 12:00 PM - 11:30 PM ',dress_code:'Casual',address:'1446 Khale Street',neighborhood:'West Seattle',phone_number:'(206) 202-0526',website:'',city_id:11,pricing:2)
